import DataSet from "@antv/data-set";
import { Chart } from "@antv/g2";
import Data from "../Data/DimensionActive";

const data = [
  { item: "การวิเคราะห์ข้อมูล", กิจกรรมการทำงาน: Data.d0 },
  { item: "การปฏิสัมพันธ์กับผู้อื่น", กิจกรรมการทำงาน: Data.d0 },
  { item: "การบริหารจัดการข้อมูล", กิจกรรมการทำงาน: Data.d0 },
  { item: "การใช้อุปกรณ์", กิจกรรมการทำงาน: Data.d0 },
  { item: "การเป็นผู้นำ", กิจกรรมการทำงาน: Data.d0 },
  { item: "การประเมินทรัพยากร", กิจกรรมการทำงาน: Data.d0 },
  { item: "การบริหารทรัพยากร", กิจกรรมการทำงาน: Data.d0 },
  { item: "การจัดระบบงาน", กิจกรรมการทำงาน: Data.d0 },
  { item: "การจัดการความสัมพันธ์บุคล", กิจกรรมการทำงาน: Data.d0 },
];
const { DataView } = DataSet;
const dv = new DataView().source(data);
dv.transform({
  type: "fold",
  fields: ["กิจกรรมการทำงาน"], // 展开字段集
  key: "user", // key字段
  value: "score", // value字段
});

const chart = new Chart({
  container: "container",
  autoFit: true,
  height: 500,
});
chart.data(dv.rows);
chart.scale("score", {
  min: 0,
  max: 80,
});
chart.coordinate("polar", {
  radius: 0.8,
});
chart.tooltip({
  shared: true,
  showCrosshairs: true,
  crosshairs: {
    line: {
      style: {
        lineDash: [4, 4],
        stroke: "#333",
      },
    },
  },
});
chart.axis("item", {
  line: null,
  tickLine: null,
  grid: {
    line: {
      style: {
        lineDash: null,
      },
    },
  },
});
chart.axis("score", {
  line: null,
  tickLine: null,
  grid: {
    line: {
      type: "line",
      style: {
        lineDash: null,
      },
    },
  },
});

chart.line().position("item*score").color("user").size(2);
chart
  .point()
  .position("item*score")
  .color("user")
  .shape("circle")
  .size(4)
  .style({
    stroke: "#fff",
    lineWidth: 1,
    fillOpacity: 1,
  });
chart.area().position("item*score").color("user");
chart.render();
