import React, { Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles, fade, withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import "./Information.css";
import InputBase from "@material-ui/core/InputBase";
import InputLabel from "@material-ui/core/InputLabel";

const useStyles = makeStyles((theme) => ({
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
  formControlTitle: {
    margin: theme.spacing(1),
    minWidth: 140,
  },
  margin: {
    margin: theme.spacing(1),
  },
}));
const BootstrapInput = withStyles((theme) => ({
  root: {
    "label + &": {
      marginTop: theme.spacing(3),
    },
  },
  input: {
    borderRadius: 4,
    position: "relative",
    backgroundColor: theme.palette.common.white,
    border: "1px solid #ced4da",
    fontSize: 16,
    padding: "10px 12px",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      "-apple-system",
      "BlinkMacSystemFont",
      '"Segoe UI"',
      "Roboto",
      '"Helvetica Neue"',
      "Arial",
      "sans-serif",
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(","),
    "&:focus": {
      boxShadow: `${fade(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
      borderColor: theme.palette.primary.main,
    },
  },
}))(InputBase);

export default function Information({ data, setInformation }) {
  const classes = useStyles();

  const { firstName, lastName, age, idCard, title, gender } = data;

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.root}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              {/* คำนำหน้าชื่อ */}
              <FormControl className={classes.margin} fullWidth>
                <InputLabel shrink htmlFor="bootstrap-input">
                  คำนำหน้า
                </InputLabel>
                <BootstrapInput
                  id="title"
                  name="title"
                  label="คำนำหน้า"
                  margin="normal"
                  variant="outlined"
                  required
                  value={title}
                  onChange={(e) =>
                    setInformation({ ...data, title: e.target.value })
                  }
                />
              </FormControl>
            </Grid>
            <Grid item md={12} xs={12}>
              {/* ชื่อ */}
              <TextField
                id="firstName"
                name="firstName"
                label="ชื่อ"
                margin="normal"
                variant="outlined"
                required
                className={classes.margin}
                fullWidth
                value={firstName}
                onChange={(e) =>
                  setInformation({ ...data, firstName: e.target.value })
                }
              />
            </Grid>
            <Grid item md={12} xs={12}>
              {/* นามสกุล */}
              <TextField
                id="lastName"
                name="lastName"
                label="นามสกุล"
                margin="normal"
                variant="filled"
                required
                fullWidth
                value={lastName}
                onChange={(e) =>
                  setInformation({ ...data, lastName: e.target.value })
                }
              />
            </Grid>
          </Grid>

          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                {/* เพศ */}
                <Grid
                  container
                  direction="row"
                  justify="flex-start"
                  alignItems="flex-start"
                  padding="2"
                >
                  <Grid container padding="2">
                    <FormLabel component="legend">เพศ</FormLabel>
                  </Grid>
                  <FormControl component="fieldset">
                    <RadioGroup
                      row
                      aria-label="gender"
                      name="gender1"
                      value={gender}
                      onChange={(e) =>
                        setInformation({ ...data, gender: e.target.value })
                      }
                    >
                      <FormControlLabel
                        value="ชาย"
                        control={<Radio color="primary" />}
                        label="ชาย"
                      />
                      <FormControlLabel
                        value="หญิง"
                        control={<Radio />}
                        label="หญิง"
                      />
                    </RadioGroup>
                  </FormControl>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                {/* อายุ */}

                <TextField
                  fullWidth
                  id="age"
                  label="อายุ(ปี)"
                  name="age"
                  margin="normal"
                  variant="filled"
                  required
                  type="number"
                  value={age}
                  onChange={(e) =>
                    setInformation({ ...data, age: e.target.value })
                  }
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                {/* เลขบัตรประจำตัวประชาชน*/}
                <TextField
                  fullWidth
                  id="idCard"
                  name="idCard"
                  label="เลขประจำตัวประชาชน 13 หลัก"
                  margin="normal"
                  type="number"
                  variant="filled"
                  required
                  value={idCard}
                  onChange={(e) =>
                    setInformation({ ...data, idCard: e.target.value })
                  }
                />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
