import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Information from "./Information";
import Education from "./Education";
import Grid from "@material-ui/core/Grid";
import ActivityWork from "./ActivityWork";
import Container from "@material-ui/core/Container";
import ContextWorks from "./ContextWorks";
import Summary from "../components/Summary";
import Data from "../Data/questionData";
import Data2 from "../Data/contentData";

const useStyles = makeStyles((theme) => ({
  root: {},
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

function getSteps() {
  return [
    "ข้อมูลส่วนบุคคล",
    "ประวัติการศึกษา",
    "กิจกรรมการทำงาน",
    "บริบทการทำงาน",
    "สรุปผลประเมิน",
  ];
}

export default function HorizontalLabelPositionBelowStepper() {
  const classes = useStyles();
  const [information, setInformation] = useState({
    title: undefined,
    firstName: undefined,
    lastName: undefined,
    idCard: undefined,
    age: undefined,
    gender: undefined,
  });

  const [educations, setEducations] = React.useState({
    educate: undefined,
    majorName: undefined,
  });

  const [values, setValues] = React.useState(Data);

  const [values2, setValues2] = React.useState(Data2);

  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = (activeStep) => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleStay = () => {};
  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const validateID = (id) => {
    if (id === undefined || id === null) {
      console.log("not have FirstName");
      return false;
    }
    if (!id.match(/^([0-9])+$/i)) {
      alert("กรอกได้เฉพาะตัวเลขเท่านั้น");
      console.log("not number");
      return false;
    }

    // eslint-disable-next-line eqeqeq
    if (id.length != 13) {
      alert("กรอกจำนวนเลขไม่ครบ");
      console.log("eror");
      return false;
    } else {
      return true;
    }
  };

  const validateFirstName = (id) => {
    if (id === undefined || id === null) {
      alert("ไม่ได้กรอกข้อมูล");
      console.log("not have FirstName");
      return false;
    }
    if (id.length > 50) {
      alert("ชื่อมีขนาดตัวอักษรเกินกว่า 50 ตัว");
      console.log("eror");
      return false;
    } else {
      return true;
    }
  };
  const validateLastName = (id) => {
    console.log("call validate", information.lastName);
    if (id === undefined || id === null) {
      alert("ไม่ได้กรอกข้อมูล");
      console.log("not have lastName");
      return false;
    }
    if (id.length > 50) {
      alert("นามสกุลมีขนาดตัวอักษรเกินกว่า 50 ตัว");
      console.log("eror");
      return false;
    } else {
      return true;
    }
  };
  const validateTitle = (id) => {
    console.log("call validate", information.title);
    if (id === undefined || id === null) {
      console.log("not have title");
      return false;
    } else {
      return true;
    }
  };
  const validateGender = (id) => {
    console.log("call validate", information.gender);
    if (id === undefined || id === null) {
      console.log("not have id");
      return false;
    } else {
      return true;
    }
  };
  const validateEducate = (id) => {
    console.log("call validate", educations.educate);
    if (id === undefined || id === null) {
      console.log("not have edu");
      return false;
    } else {
      return true;
    }
  };
  const validateMajor = (id) => {
    console.log("call validate", educations.majorName);
    if (id === undefined || id === null) {
      console.log("not have major");
      return false;
    } else {
      return true;
    }
  };

  const validateFirstStep = () => {
    if (
      validateTitle(information.title) &&
      validateGender(information.gender) &&
      validateFirstName(information.firstName) &&
      validateLastName(information.lastName) &&
      validateID(information.idCard)
    ) {
      return true;
    }
  };

  const validateTwoStep = () => {
    if (
      validateEducate(educations.educate) &&
      validateMajor(educations.majorName)
    ) {
      return true;
    }
  };

  const onSubmit = (step) => {
    console.log("call OnSubmit");
    console.log("call OnSubmitStep", step);
    console.log("call Onfirst", validateFirstStep());
    console.log("call Onfirst", validateTwoStep());
    if (step === 0) {
      if (validateFirstStep()) {
        return true;
      }
    }
    if (step === 1) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 2) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 3) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 4) {
      if (validateTwoStep()) {
        return true;
      }
    }
  };

  function getStepContent(stepIndex) {
    console.log(information);
    console.log(educations);
    console.log(values);
    console.log(values2);

    switch (stepIndex) {
      case 0:
        return (
          <Information data={information} setInformation={setInformation} />
        );
      case 1:
        return <Education data={educations} setEducations={setEducations} />;
      case 2:
        return <ActivityWork data={values} setValues={setValues} />;
      case 3:
        return <ContextWorks data={values2} setValues={setValues2} />;
      case 4:
        return <Summary />;

      default:
        return "Unknown stepIndex";
    }
  }

  return (
    <div className={classes.root}>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      <Container fixed>
        <Grid container direction="row" justify="center" alignItems="center">
          <div>
            <div>
              {activeStep === steps.length ? (
                <div>
                  <Typography className={classes.instructions}>
                    สมบูรณ์
                  </Typography>
                </div>
              ) : (
                <div>
                  <Typography className={classes.instructions}>
                    {getStepContent(activeStep)}
                  </Typography>
                  <div>
                    <Button
                      type="submit"
                      disabled={activeStep === 0 || activeStep === 4}
                      onClick={handleBack}
                      className={classes.backButton}
                    >
                      ย้อนกลับ
                    </Button>

                    <Button
                      variant="contained"
                      color="primary"
                      type="submit"
                      onClick={onSubmit(activeStep) ? handleNext : handleStay}
                    >
                      {activeStep === steps.length - 1 ? "เสร็จสิ้น" : "ถัดไป"}
                    </Button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </Grid>
      </Container>
    </div>
  );
}
