import React, { useEffect, useReducer } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Information from "./Information";
import Education from "./Education";
import Grid from "@material-ui/core/Grid";
import ActivityWork from "./ActivityWork";
import Container from "@material-ui/core/Container";
import ContextWorks from "./ContextWorks";
import Summary from "./Summary";
import Data from "../Data/questionData";
import Data2 from "../Data/contentData";
import DataTable from "../Data/tableData";
import DataActive from "../Data/DimensionActive";
import DataContent from "../Data/DimensionContent";
import get from "../service/get";
const useStyles = makeStyles((theme) => ({
  root: {},
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

function getSteps() {
  return [
    "ข้อมูลส่วนบุคคล",
    "ประวัติการศึกษา",
    "กิจกรรมการทำงาน",
    "บริบทการทำงาน",
    "สรุปการประเมิน",
  ];
}

export default function HorizontalLabelPositionBelowStepper() {
  const classes = useStyles();

  // setStateReact
  const [information, setInformation] = React.useState({
    title: undefined,
    firstName: undefined,
    lastName: undefined,
    idCard: undefined,
    age: undefined,
    gender: undefined,
  });

  const [educations, setEducations] = React.useState({
    educate: undefined,
    majorName: undefined,
  });
  // const [active, setActive] = React.useState(DataCal);
  const [values, setValues] = React.useState(Data);

  const [values2, setValues2] = React.useState(Data2);

  const [active, setActive] = React.useState(DataActive);

  const [content, setContent] = React.useState(DataContent);

  const [activeStep, setActiveStep] = React.useState(0);
  const [summary, setSummary] = React.useState(DataTable);

  const reducer = (state, payload) => ({ ...state, ...payload });
  const [data1, setData1] = useReducer(reducer, {
    users: [],
    isFetching: false,
  });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        setData1({ users: data1.users, isFetching: true });
        const response = await get();
        setData1({ users: response, isFetching: false });
      } catch (response) {
        console.log(response.status);
        alert(response.status);
        setData1({ users: data1.users, isFetching: false });
      }
    };

    fetchUsers();
    // eslint-disable-next-line
  }, []);
  // เกี่ยวกับ stepper
  const steps = getSteps();

  const handleNext = (activeStep) => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };
  const [error, setError] = React.useState({
    titleError: undefined,
    firstError: undefined,
    lastError: undefined,
    idError: undefined,
    ageError: undefined,
  });
  const handleStay = () => {
    if (!validateTitle(information.title)) {
      alert("กรอกข้อมลูไม่ครบ: คำนำ");
      setError({ titleError: false });
      return false;
    }
    if (!validateFirstName(information.firstName)) {
      alert("กรอกข้อมลูไม่ครบ: ชื่อ");
      setError({ firstError: false });
      return false;
    }

    if (!validateLastName(information.lastName)) {
      alert("กรอกข้อมลูไม่ครบ: นามสกุล");
      setError({ lastError: false });
      return false;
    }
    if (!validateGender(information.gender)) {
      alert("กรอกข้อมลูไม่ครบ: อายุ");
      return false;
    }
    if (!validateID(information.idCard)) {
      alert("กรอกข้อมลูบัตรประชาชนผิด กรอกใหม่อีกครั้ง");
      setError({ idError: false });
      return false;
    }
    if (!validateAge(information.age)) {
      alert("กรอกข้อมลูไม่ถูกต้อง : อายุต้องอยู่ระหว่าง 11-70 ปี ");
      setError({ ageError: false });
      return false;
    }
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  //  validate
  const validateID = (id) => {
    if (id === undefined || id === null) {
      return false;
    }

    // eslint-disable-next-line eqeqeq
    if (id.length != 13) {
      return false;
    }
    if (id.length === 13) {
      var i, sum;
      for (i = 0, sum = 0; i < 12; i++)
        sum += parseFloat(id.charAt(i)) * (13 - i);
      if ((11 - (sum % 11)) % 10 !== parseFloat(id.charAt(12))) {
        alert("เลขบัตรประจำตัวประชาชนผิด");
        return false;
      }
      return true;
    }
  };

  const validateFirstName = (id) => {
    if (id === undefined || id === null) {
      return false;
    }
    if (id.length > 50) {
      alert("ชื่อมีขนาดตัวอักษรเกินกว่า 50 ตัว");
      return false;
    } else {
      return true;
    }
  };

  const validateLastName = (id) => {
    if (id === undefined || id === null) {
      console.log("not have lastName");
      return false;
    }
    if (id.length > 50) {
      alert("นามสกุลมีขนาดตัวอักษรเกินกว่า 50 ตัว");
      console.log("eror");
      return false;
    } else {
      return true;
    }
  };
  const validateTitle = (id) => {
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };
  const validateGender = (id) => {
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };
  const validateAge = (id) => {
    if (id === undefined || id === null) {
      return false;
    }
    if (id < 10) {
      return false;
    }
    if (id > 71) {
      return false;
    } else {
      return true;
    }
  };
  const validateEducate = (id) => {
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };
  const validateMajor = (id) => {
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };

  const validateFirstStep = () => {
    if (
      validateTitle(information.title) &&
      validateGender(information.gender) &&
      validateFirstName(information.firstName) &&
      validateLastName(information.lastName) &&
      validateID(information.idCard) &&
      validateAge(information.age)
    ) {
      return true;
    }
  };

  const validateTwoStep = () => {
    if (
      validateEducate(educations.educate) &&
      validateMajor(educations.majorName)
    ) {
      return true;
    }
  };

  // ทำ validate ในแต่ละหน้า
  const onSubmit = (step) => {
    if (step === 0) {
      if (validateFirstStep()) {
        return true;
      }
    }
    if (step === 1) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 2) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 3) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 4) {
      if (validateTwoStep()) {
        return true;
      }
    }
  };

  function getStepContent(stepIndex) {
    console.log(information.title);
    console.log(error);
    switch (stepIndex) {
      case 0:
        return (
          <Information
            data={information}
            setInformation={setInformation}
            validateTitle={validateTitle}
            error={error}
            setError={setError}
          />
        );
      case 1:
        return <Education data={educations} setEducations={setEducations} />;
      case 2:
        return (
          <ActivityWork
            data={values}
            data1={data1}
            setValues={setValues}
            summary={summary}
            setSummary={setSummary}
            active={active}
            setActive={setActive}
          />
        );
      case 3:
        return (
          <ContextWorks
            data1={data1}
            data={values2}
            setValues2={setValues2}
            summary={summary}
            setSummary={setSummary}
            content={content}
            setContent={setContent}
          />
        );
      case 4:
        return (
          <Summary
            name={information.firstName}
            lastName={information.lastName}
            titles={information.title}
            cardID={information.idCard}
          />
        );

      default:
        return "Unknown stepIndex";
    }
  }

  return (
    <div className={classes.root}>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      <Container fixed>
        <Grid container direction="row" justify="center" alignItems="center">
          {activeStep === steps.length ? (
            <div>
              <h1>ขอบคุณค่ะ สำหรับการเข้าร่วมทำแบบประเมิน</h1>
            </div>
          ) : (
            <div>
              <Typography className={classes.instructions}>
                {getStepContent(activeStep)}
              </Typography>

              <Button
                type="submit"
                disabled={activeStep === 0 || activeStep === 4}
                onClick={handleBack}
                className={classes.backButton}
              >
                ย้อนกลับ
              </Button>

              <Button
                variant="contained"
                color="primary"
                type="submit"
                onClick={onSubmit(activeStep) ? handleNext : handleStay}
              >
                {activeStep === steps.length - 1 ? "เสร็จสิ้น" : "ถัดไป"}
              </Button>
            </div>
          )}
        </Grid>
      </Container>
    </div>
  );
}
