import React, { Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles, fade } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import "./Information.css";

const useStyles = makeStyles((theme) => ({
  root: {
    "& input": {
      backgroundColor: "fcfcfb",
    },
    border: "1px solid #e2e2e1",
    overflow: "hidden",
    borderRadius: 4,
    backgroundColor: "#fcfcfb",
    transition: theme.transitions.create(["border-color", "box-shadow"]),
    "&:hover": {
      backgroundColor: "#fff",
    },
    "&$focused": {
      backgroundColor: "#fff",
      boxShadow: `${fade(theme.palette.primary.main, 0.25)} 0 0 0 2px`,
      borderColor: theme.palette.primary.main,
    },
  },
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
  formControlTitle: {
    margin: theme.spacing(1),
    minWidth: 140,
  },
}));
function RedditTextField(props) {
  const classes = useStyles();

  return (
    <TextField InputProps={{ classes, disableUnderline: true }} {...props} />
  );
}

export default function Information({
  data,
  setInformation,
  validateTitle,
  error,
  setError,
}) {
  const classes = useStyles();

  const { firstName, lastName, age, idCard, title, gender } = data;

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              {/* คำนำหน้าชื่อ */}

              {(!(title === "") && error.titleError === undefined) ||
              error.titleError === true ? (
                <RedditTextField
                  id="title"
                  name="title"
                  label="คำนำหน้า"
                  margin="normal"
                  required
                  fullWidth
                  className={classes.root}
                  variant="filled"
                  value={title}
                  onChange={(e) =>
                    setInformation({ ...data, title: e.target.value })
                  }
                />
              ) : (
                <div>
                  <RedditTextField
                    id="title"
                    name="title"
                    label="คำนำหน้า"
                    margin="normal"
                    required
                    fullWidth
                    className={classes.root}
                    variant="filled"
                    value={title}
                    onChange={(e) =>
                      setInformation({ ...data, title: e.target.value })
                    }
                    error
                  />
                  <FormHelperText id="component-error-text" error>
                    กรอกคำนำหน้าชื่อ
                  </FormHelperText>
                </div>
              )}
            </Grid>
            <Grid item md={12} xs={12}>
              {/* ชื่อ */}
              {(!(firstName === "") && error.firstError === undefined) ||
              error.firstError === true ? (
                <TextField
                  id="firstName"
                  name="firstName"
                  label="ชื่อ"
                  margin="normal"
                  variant="filled"
                  required
                  fullWidth
                  className={classes.root}
                  value={firstName}
                  onChange={(e) =>
                    setInformation({ ...data, firstName: e.target.value })
                  }
                />
              ) : (
                <div>
                  <TextField
                    id="firstName"
                    name="firstName"
                    label="ชื่อ"
                    margin="normal"
                    variant="filled"
                    required
                    fullWidth
                    className={classes.root}
                    value={firstName}
                    onChange={(e) =>
                      setInformation({ ...data, firstName: e.target.value })
                    }
                    error
                  />
                  <FormHelperText id="component-error-text" error>
                    กรอกชื่อจริง
                  </FormHelperText>
                </div>
              )}
            </Grid>
            <Grid item md={12} xs={12}>
              {/* นามสกุล */}
              {(!(lastName === "") && error.lastError === undefined) ||
              error.lastError === true ? (
                <TextField
                  id="lastName"
                  name="lastName"
                  label="นามสกุล"
                  margin="normal"
                  variant="filled"
                  required
                  fullWidth
                  className={classes.root}
                  value={lastName}
                  onChange={(e) =>
                    setInformation({ ...data, lastName: e.target.value })
                  }
                />
              ) : (
                <div>
