import React, { Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import "./Information.css";

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: "white",
    borderRadius: 6,
  },
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
  },
  formControlTitle: {
    margin: theme.spacing(1),
    minWidth: 140,
  },
}));
const ValidationTextField = withStyles({
  root: {
    "& input:valid + fieldset": {
      borderColor: "green",
      borderWidth: 2,
    },
    "& input:invalid + fieldset": {
      borderColor: "red",
      borderWidth: 2,
    },
    "& input:valid:focus + fieldset": {
      borderLeftWidth: 6,
      padding: "4px !important", // override inline-style
    },
  },
})(TextField);

export default function Information({ data, setInformation }) {
  const classes = useStyles();

  const { firstName, lastName, age, idCard, title, gender } = data;

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              {/* คำนำหน้าชื่อ */}
              <TextField
                id="title"
                name="title"
                label="คำนำหน้า"
                margin="normal"
                required
                fullWidth
                className={classes.root}
                variant="outlined"
                value={title}
                onChange={(e) =>
                  setInformation({ ...data, title: e.target.value })
                }
              />
            </Grid>
            <Grid item md={12} xs={12}>
              {/* ชื่อ */}
              <TextField
                id="firstName"
                name="firstName"
                label="ชื่อ"
                margin="normal"
                variant="outlined"
                required
                fullWidth
                value={firstName}
                onChange={(e) =>
                  setInformation({ ...data, firstName: e.target.value })
                }
              />
            </Grid>
            <Grid item md={12} xs={12}>
              {/* นามสกุล */}
              <TextField
                id="lastName"
                name="lastName"
                label="นามสกุล"
                margin="normal"
                variant="outlined"
                required
                fullWidth
                value={lastName}
                onChange={(e) =>
                  setInformation({ ...data, lastName: e.target.value })
                }
              />
            </Grid>
          </Grid>

          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                {/* เพศ */}
                <Grid
                  container
                  direction="row"
                  justify="flex-start"
                  alignItems="flex-start"
                  padding="2"
                >
                  <Grid container padding="2">
                    <FormLabel component="legend">เพศ</FormLabel>
                  </Grid>
                  <FormControl component="fieldset">
                    <RadioGroup
                      row
                      aria-label="gender"
                      name="gender1"
                      value={gender}
                      onChange={(e) =>
                        setInformation({ ...data, gender: e.target.value })
                      }
                    >
                      <FormControlLabel
                        value="ชาย"
                        control={<Radio color="primary" />}
                        label="ชาย"
                      />
                      <FormControlLabel
                        value="หญิง"
                        control={<Radio />}
                        label="หญิง"
                      />
                    </RadioGroup>
                  </FormControl>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                {/* อายุ */}

                <TextField
                  fullWidth
                  id="age"
                  label="อายุ(ปี)"
                  name="age"
                  margin="normal"
                  variant="outlined"
                  required
                  type="number"
                  value={age}
                  onChange={(e) =>
                    setInformation({ ...data, age: e.target.value })
                  }
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid container direction="row" spacing={1}>
            <Grid item md={12} xs={12}>
              <Grid item md={12} xs={12}>
                {/* เลขบัตรประจำตัวประชาชน*/}
                <TextField
                  fullWidth
                  id="idCard"
                  name="idCard"
                  label="เลขประจำตัวประชาชน 13 หลัก"
                  margin="normal"
                  type="number"
                  variant="outlined"
                  required
                  value={idCard}
                  onChange={(e) =>
                    setInformation({ ...data, idCard: e.target.value })
                  }
                />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
