import React, { Fragment, useState } from "react";
import { KeyboardDatePicker } from "@material-ui/pickers";
import { MuiPickersUtilsProvider } from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import FindInPageIcon from "@material-ui/icons/FindInPage";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));

function DatePickers() {
  const classes = useStyles();

  return (
    <form className={classes.container} noValidate>
      <TextField
        id="date"
        label="วันที่เริ่มต้น"
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField
        id="date"
        label="วันที่สิ้นสุด"
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      />
    </form>
  );
}
function KeyboardDatePickerExample({ data, setDate }) {
  const [selectedStartDate, handleStartDateChange] = useState();
  const [selectedEndDate, handleEndDateChange] = useState();
  const onSubmit = () => {
    console.log(selectedStartDate);
    console.log(selectedEndDate);
    const submit = [{ startdate: selectedStartDate, enddate: selectedEndDate }];
    console.log("วันที่", submit);
    setDate(submit);
    console.log("data", data);
  };
  return (
    <Fragment>
      <Container maxWidth="lg">
        <Grid container spacing={1}>
          {/* Chart */}
          <Grid item xs={4} md={4} lg={4}>
            <DatePickers />
            <MuiPickersUtilsProvider>
              <KeyboardDatePicker
                label={<p>กรอกวันที่เริ่มต้น</p>}
                clearable
                value={selectedStartDate}
                placeholder="01/01/2020"
                onChange={(value) => handleStartDateChange(value)}
                format="MM/dd/yyyy"
              />
            </MuiPickersUtilsProvider>
          </Grid>
          {/* Recent Deposits */}
          <Grid item xs={3} md={3} lg={3}>
            <MuiPickersUtilsProvider>
              <KeyboardDatePicker
                label={<p>กรอกวันที่สิ้นสุด</p>}
                placeholder="31/12/2020"
                value={selectedEndDate}
                onChange={(value) => handleEndDateChange(value)}
                format="dd/MM/yyyy"
              />
            </MuiPickersUtilsProvider>
          </Grid>
          <Grid
            item
            xs={4}
            md={4}
            lg={4}
            alignContent="center"
            justify="center"
          >
            <Button variant="outlined" color="primary" onClick={onSubmit}>
              <FindInPageIcon />
              <p>ค้นหาข้อมูล</p>
            </Button>
          </Grid>
        </Grid>
      </Container>
    </Fragment>
  );
}

export default KeyboardDatePickerExample;
