import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import SelectEducation from "../components/SelectEducation";
import SelectMajor from "../components/SelectMajor";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";

// สร้างstyle
const useStyles = makeStyles((theme) => ({
  formControl: {
    minWidth: 320,
  },
}));

export default function SimpleSelect({
  formProps: { register, errors },
  data,
  setEducations,
}) {
  const classes = useStyles();
  const { educate } = data;
  const options = education.map((option) => {
    const firstLetter = option.education[0].toUpperCase();
    return {
      firstLetter: /[0-9]/.test(firstLetter) ? "0-9" : firstLetter,
      ...option,
    };
  });

  return (
    <Fragment>
      <h1>ประวัติการศึกษา</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div className={classes.formControl}>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                padding="2"
              >
                <Autocomplete
                  value={educate}
                  onChange={(e, value) =>
                    setEducations({ ...data, educate: value })
                  }
                  options={options.sort(
                    (ก, อ) => -อ.firstLetter.localeCompare(ก.firstLetter)
                  )}
                  groupBy={(option) => option.firstLetter}
                  getOptionLabel={(option) => option.education}
                  fullWidth
                  id="auto-complete"
                  autoComplete
                  includeInputInList
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="ระดับการศึกษา"
                      margin="normal"
                    />
                  )}
                />
              </Grid>
            </Grid>
            <Grid item md={12} xs={12}>
              <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                padding="2"
              >
                <SelectMajor inputRef={register} error={!!errors.majorName} />
              </Grid>
            </Grid>
          </Grid>
        </div>
      </Grid>
    </Fragment>
  );
}
const education = [
  { id_education: 1, education: "ประถมศึกษา" },
  { id_education: 2, education: "มัธยมต้น" },
  { id_education: 3, education: "มัธยมปลาย" },
  { id_education: 4, education: "ปวช." },
  { id_education: 5, education: "ปวท." },
  { id_education: 6, education: "ปวส." },
  { id_education: 7, education: "ประกาศนียบัตร" },
  { id_education: 8, education: "ประกาศนียบัตรบัณฑิต" },
  { id_education: 9, education: "ประกาศนียบัตรบัณฑิตขั้นสูง" },
  { id_education: 10, education: "อนุปริญญา" },
  { id_education: 11, education: "ปริญญาตรี" },
  { id_education: 13, education: "ปริญญาโท" },
  { id_education: 13, education: "ปริญญาเอก" },
];
