import React from "react";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
const useStyles = makeStyles((theme) => ({
  label: {
    margin: 0,
  },
}));

export default function RadioButtons(props) {
  const { sub1, sub2, header, description, handleChange, index, value } = props;
  const classes = useStyles();
  const renderRadio = [0, 1, 2, 3, 4, 5, 6].map((point, currentindex) => {
    return (
      <FormControlLabel
        key={currentindex}
        value={point + 1}
        checked={currentindex === value}
        onClick={(e) => handleChange(point, index)}
        control={<Radio color="primary" />}
        label={point + 1}
        className={classes.label}
        labelPlacement="top"
      />
    );
  });
  return (
    <div>
      <Box bgcolor="background.paper" spacing={2} justifyContent="center">
        <FormControl component="fieldset">
          <Grid container xs={12} justifyContent="center" alignContent="center">
            <h3 align="left"> &nbsp;{header}</h3>
            <Grid
              container
              xs={12}
              justifyContent="center"
              alignContent="center"
            >
              <h4 align="left"> &nbsp;&nbsp;&nbsp;{description}</h4>
            </Grid>
            <Grid container xs={6} justify="flex-start" alignItems="center">
              <h5>
                <div className="leftfloat">{sub1}</div>
              </h5>
            </Grid>
            <Grid container xs={6} justify="flex-end" alignItems="center">
              <h5>
                <div className="rightfloat">{sub2}</div>
              </h5>
            </Grid>

            <Grid
              container
              xs={12}
              justifyContent="center"
              alignContent="center"
            >
              <div className="cen">
                <RadioGroup row aria-label="ch1" name="ch1">
                  <Box justifyContent="center" alignContent="center" maxWidth>
                    {renderRadio}
                  </Box>
                </RadioGroup>
              </div>
            </Grid>
          </Grid>
        </FormControl>
      </Box>
    </div>
  );
}
