import React, { useRef } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import SaveIcon from "@material-ui/icons/Save";
import Container from "@material-ui/core/Container";
import Button from "@material-ui/core/Button";
import { useReactToPrint } from "react-to-print";
import Table from "../components/Table/Table";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
}));

export default function Summary(props) {
  const classes = useStyles();
  const componentRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });
  const { name, lastname, cardID, titles, data } = props;
  return (
    <Container maxWidth="sm" alignItems="center">
      <div>
        <Grid
          container
          direction="row"
          justify="center"
          alignItems="center"
          spacing={1}
        >
          <div className={classes.root}>
            <Grid
              container
              direction="row"
              justify="center"
              alignItems="center"
              spacing={1}
              ref={componentRef}
            >
              <Grid item md={12} xs={12}>
                <Grid>
                  <Container fixed alignItems="center">
                    <div alignItems="center">
                      <h1>สรุปผลประเมิน</h1>
                      <h1>ความสนใจที่มีต่อหน่วยงานราชการ</h1>
                      <h3>
                        ชื่อ {titles} {name} นามสกุล {lastname}
                      </h3>
                      <h4>เลขบัตรประจำตัวประชาชน {cardID}</h4>
                    </div>
                    <Table data={data} />
                  </Container>
                </Grid>
              </Grid>
            </Grid>
            <Grid container direction="row" spacing={1}>
              <Grid container spacing={4}>
                <Grid item xs={5}>
                  <Button
                    variant="contained"
                    color="secondary"
                    size="large"
                    className={classes.button}
                    startIcon={<SaveIcon />}
                    alignItems="left"
                    onClick={handlePrint}
                  >
                    Print
                  </Button>
                </Grid>
              </Grid>
            </Grid>
          </div>
        </Grid>
      </div>
    </Container>
  );
}
