import React from "react";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
const useStyles = makeStyles({
  root: {
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  icon: {
    borderRadius: "50%",
    width: 16,
    height: 16,
    boxShadow:
      "inset 0 0 0 1px rgba(16,22,26,.2), inset 0 -1px 0 rgba(16,22,26,.1)",
    backgroundColor: "#f5f8fa",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.8),hsla(0,0%,100%,0))",
    "$root.Mui-focusVisible &": {
      outline: "2px auto rgba(19,124,189,.6)",
      outlineOffset: 2,
    },
    "input:hover ~ &": {
      backgroundColor: "#ebf1f5",
    },
    "input:disabled ~ &": {
      boxShadow: "none",
      background: "rgba(206,217,224,.5)",
    },
  },
  checkedIcon: {
    backgroundColor: "#137cbd",
    backgroundImage:
      "linear-gradient(180deg,hsla(0,0%,100%,.1),hsla(0,0%,100%,0))",
    "&:before": {
      display: "block",
      width: 16,
      height: 16,
      backgroundImage: "radial-gradient(#fff,#fff 28%,transparent 32%)",
      content: '""',
    },
    "input:hover ~ &": {
      backgroundColor: "#106ba3",
    },
  },
});

export default function RadioButtons(props) {
  const classes = useStyles();

  const { header, description, handleChange, index, value } = props;

  const renderRadio = [0, 1, 2, 3, 4, 5, 6].map((point, currentindex) => {
    return (
      <FormControlLabel
        key={currentindex}
        value={point + 1}
        checked={currentindex === value}
        onClick={(e) => handleChange(point, index)}
        control={<Radio color="primary" />}
        label={point + 1}
      />
    );
  });
  return (
    <div>
      <Box bgcolor="background.paper" spacing={10}>
        <FormControl component="fieldset">
          <Grid container xs={10}>
            <h3 align="left">{header}</h3>

            <h4 align="left">{description}</h4>

            <RadioGroup
              row
              aria-label="ch1"
              name="ch1"
              className={classes.root}
              disableRipple
              color="default"
              checkedIcon={
                <span className={clsx(classes.icon, classes.checkedIcon)} />
              }
              icon={<span className={classes.icon} />}
            >
              {renderRadio}
            </RadioGroup>
          </Grid>
        </FormControl>
      </Box>
    </div>
  );
}
