import React, { Fragment } from "react";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import FormControl from "@material-ui/core/FormControl";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";

import "../../Css/Information.css";

const useStyles = makeStyles((theme) => ({
  root: {
    "& input": {
      backgroundColor: "#F5F5F5",
      fontFamily: "Prompt",
    },
    "& label": {
      fontFamily: "Prompt",
    },
  },
  formControl: {
    alignContent: "left",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
    fontFamily: "Prompt",
    "& legend": {
      fontFamily: "Prompt",
    },
  },
  TextFieldControl: {
    marginLeft: theme.spacing(1),
    fontFamily: "Prompt",
  },
  formControlTitle: {
    margin: theme.spacing(1),
    minWidth: 140,
    fontFamily: "Prompt",
  },
  formGender: {
    fontFamily: "Prompt",
    "& legend": {
      fontFamily: "Prompt",
    },
    "& label": {
      fontFamily: "Prompt",
    },
  },
}));

export default function Information({
  data,
  setInformation,
  validateTitle,
  error,
  setError,
}) {
  const classes = useStyles();

  const { firstName, lastName, age, idCard, title, gender } = data;

  return (
    <Fragment>
      <h1>ข้อมูลส่วนบุคคล</h1>
      <Grid
        container
        direction="row"
        justify="center"
        alignItems="center"
        spacing={1}
      >
        <div>
          <Grid
            container
            direction="row"
            justify="center"
            alignItems="center"
            spacing={1}
          >
            <Grid item md={12} xs={12}>
              {/* คำนำหน้าชื่อ */}

              {error.titleError === undefined || error.titleError === true ? (
                <TextField
                  id="title"
                  name="title"
                  label="คำนำหน้า"
                  margin="normal"
                  required
                  fullWidth
                  className={classes.root}
                  variant="filled"
                  value={title}
                  onChange={(e) =>
                    setInformation({ ...data, title: e.target.value })
                  }
                />
              ) : (
                <div>
                  <TextField
                    id="title"
                    name="title"
                    label="คำนำหน้า"
                    margin="normal"
                    required
                    fullWidth
                    className={classes.root}
                    variant="filled"
                    value={title}
                    onChange={(e) =>
                      setInformation({ ...data, title: e.target.value })
                    }
                    error
                  />
                  <FormHelperText
                    id="component-error-text"
                    error
                    className={classes.formGender}
                  >
                    กรอกคำนำหน้าชื่อ
                  </FormHelperText>
                </div>
              )}
            </Grid>
            <Grid item md={12} xs={12}>
              {/* ชื่อ */}
              {error.firstError === undefined || error.firstError === true ? (
                <TextField
                  id="firstName"
                  name="firstName"
                  label="ชื่อ"
                  margin="normal"
                  variant="filled"
                  required
                  fullWidth
                  className={classes.root}
                  value={firstName}
                  onChange={(e) =>
                    setInformation({ ...data, firstName: e.target.value })
                  }
                />
              ) : (
                <div>
                  <TextField
                    id="firstName"
                    name="firstName"
                    label="ชื่อ"
                    margin="normal"
                    variant="filled"
                    required
                    fullWidth
                    className={classes.root}
                    value={firstName}
                    onChange={(e) =>
                      setInformation({ ...data, firstName: e.target.value })
                    }
                    error
                  />
                  <FormHelperText
                    id="component-error-text"
                    error
                    className={classes.formGender}
                  >
