import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Information from "./Information";
import Education from "./Education";
import Grid from "@material-ui/core/Grid";
import ActivityWork from "./ActivityWork";
import Container from "@material-ui/core/Container";
import ContextWorks from "./ContextWorks";
import Summary from "../components/Summary";
import Data from "../Data/questionData";
import Data2 from "../Data/contentData";
import Chart from "./summaryChart";
import DataTable from "../Data/tableData";

const useStyles = makeStyles((theme) => ({
  root: {},
  backButton: {
    marginRight: theme.spacing(1),
  },
  instructions: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
  },
}));

function getSteps() {
  return [
    "ข้อมูลส่วนบุคคล",
    "ประวัติการศึกษา",
    "กิจกรรมการทำงาน",
    "บริบทการทำงาน",
    "สรุปการประเมิน",
  ];
}

export default function HorizontalLabelPositionBelowStepper() {
  const classes = useStyles();

  // setStateReact
  const [information, setInformation] = useState({
    title: undefined,
    firstName: undefined,
    lastName: undefined,
    idCard: undefined,
    age: undefined,
    gender: undefined,
  });

  const [educations, setEducations] = React.useState({
    educate: undefined,
    majorName: undefined,
  });
  // const [active, setActive] = React.useState(DataCal);
  const [values, setValues] = React.useState(Data);

  const [values2, setValues2] = React.useState(Data2);

  const [activeStep, setActiveStep] = React.useState(0);
  const [summary, setSummary] = React.useState(DataTable);

  // เกี่ยวกับ stepper
  const steps = getSteps();

  const handleNext = (activeStep) => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleStay = () => {};

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  //  validate
  const validateID = (id) => {
    if (id === undefined || id === null) {
      return false;
    }

    // eslint-disable-next-line eqeqeq
    if (id.length != 13) {
      return false;
    }
    if (id.length === 13) {
      var i, sum;
      for (i = 0, sum = 0; i < 12; i++)
        sum += parseFloat(id.charAt(i)) * (13 - i);
      if ((11 - (sum % 11)) % 10 !== parseFloat(id.charAt(12))) {
        alert("เลขบัตรประจำตัวประชาชนผิด");
        return false;
      }
      return true;
    }
  };

  const validateFirstName = (id) => {
    if (id === undefined || id === null) {
      return false;
    }
    if (id.length > 50) {
      alert("ชื่อมีขนาดตัวอักษรเกินกว่า 50 ตัว");
      return false;
    } else {
      return true;
    }
  };

  const validateLastName = (id) => {
    console.log("call validate", information.lastName);
    if (id === undefined || id === null) {
      console.log("not have lastName");
      return false;
    }
    if (id.length > 50) {
      alert("นามสกุลมีขนาดตัวอักษรเกินกว่า 50 ตัว");
      console.log("eror");
      return false;
    } else {
      return true;
    }
  };
  const validateTitle = (id) => {
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };
  const validateGender = (id) => {
    console.log("call validate", information.gender);
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };
  const validateAge = (id) => {
    console.log("call validate", information.age);
    if (id === undefined || id === null) {
      return false;
    }
    if (id < 10) {
      return false;
    }
    if (id > 71) {
      return false;
    } else {
      return true;
    }
  };
  const validateEducate = (id) => {
    console.log("call validate", educations.educate);
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };
  const validateMajor = (id) => {
    console.log("call validate", educations.majorName);
    if (id === undefined || id === null) {
      return false;
    } else {
      return true;
    }
  };

  const validateFirstStep = () => {
    if (
      validateTitle(information.title) &&
      validateGender(information.gender) &&
      validateFirstName(information.firstName) &&
      validateLastName(information.lastName) &&
      validateID(information.idCard) &&
      validateAge(information.age)
    ) {
      return true;
    }
  };

  const validateTwoStep = () => {
    if (
      validateEducate(educations.educate) &&
      validateMajor(educations.majorName)
    ) {
      return true;
    }
  };

  // ทำ validate ในแต่ละหน้า
  const onSubmit = (step) => {
    console.log(values);
    console.log(values2);
    if (step === 0) {
      if (validateFirstStep()) {
        return true;
      }
    }
    if (step === 1) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 2) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 3) {
      if (validateTwoStep()) {
        return true;
      }
    }
    if (step === 4) {
      if (validateTwoStep()) {
        return true;
      }
    }
  };

  function getStepContent(stepIndex) {
    console.log(summary);
    switch (stepIndex) {
      case 0:
        return (
          <Information data={information} setInformation={setInformation} />
        );
      case 1:
        return <Education data={educations} setEducations={setEducations} />;
      case 2:
        return (
          <ActivityWork
            data={values}
            setValues={setValues}
            summary={summary}
            setSummary={setSummary}
          />
        );
      case 3:
        return (
          <ContextWorks
            data={values2}
            setValues2={setValues2}
            summary={summary}
            setSummary={setSummary}
          />
        );
      case 4:
        return (
          <Summary
            data={summary}
            name={information.firstName}
            lastName={information.lastName}
            titles={information.title}
            cardID={information.idCard}
          />
        );

      default:
        return "Unknown stepIndex";
    }
  }

  return (
    <div className={classes.root}>
      <Stepper activeStep={activeStep} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      <Container fixed>
        <Grid container direction="row" justify="center" alignItems="center">
          {activeStep === steps.length ? (
            <div>
              <Chart />
              <h1>ขอบคุณค่ะ สำหรับการเข้าร่วมทำแบบประเมิน</h1>
            </div>
          ) : (
            <div>
              <Typography className={classes.instructions}>
                {getStepContent(activeStep)}
              </Typography>

              <Button
                type="submit"
                disabled={activeStep === 0 || activeStep === 4}
                onClick={handleBack}
                className={classes.backButton}
              >
                ย้อนกลับ
              </Button>

              <Button
                variant="contained"
                color="primary"
                type="submit"
                onClick={onSubmit(activeStep) ? handleNext : handleStay}
              >
                {activeStep === steps.length - 1 ? "เสร็จสิ้น" : "ถัดไป"}
              </Button>
            </div>
          )}
        </Grid>
      </Container>
    </div>
  );
}
