import React, { useState } from "react";
import Button from "./component/Button";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import BackgroundHeader from "./pic/001-02.png";
import Link from "@material-ui/core/Link";
import { makeStyles } from "@material-ui/core/styles";
import "./index.css";
import logo from "./pic/ocsc-logo_7.png";
// import data
import Data from "./Data/informationData";
import { loginAuth } from "jwt-auth-react";

const useStyles = makeStyles((theme) => ({
  root: {
    height: "100vh",
  },
  image: {
    backgroundImage: "url(" + BackgroundHeader + ")",
    backgroundRepeat: "no-repeat",

    backgroundColor:
      theme.palette.type === "light"
        ? theme.palette.grey[50]
        : theme.palette.grey[900],
    backgroundSize: "cover",
    backgroundPosition: "center",
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function Information() {
  const classes = useStyles();
  const [setInformation] = useState(Data);
  const [loginId, setLogin] = useState({
    user: "",
    password: "",
    authen: false,
  });

  function onLogin() {
    console.log(loginId);
    if (loginId.user === "admin" && loginId.password === "1234") {
      loginId.authen = true;
      setInformation(loginId);
    }
  }
  console.log(loginId);
  return (
    <Grid
      container
      position="center"
      justify="center"
      alignContent="center"
      component="main"
      className={classes.root}
    >
      <Grid
        item
        position="center"
        xs={12}
        sm={8}
        md={5}
        component={Paper}
        elevation={8}
      >
        <div className={classes.paper}>
          <Box mt={5}>
            <img src={logo} width={300} alt="logo" />
          </Box>

          <h2>ระบบประเมินความสนใจของบุคคลที่มีต่อหน่วยงานราชการไทย</h2>
          <form className={classes.form} Validate>
            <TextField
              variant="outlined"
              margin="normal"
              fullWidth
              id="username"
              label={<p>รหัสผู้ใช้</p>}
              name="username"
              autoComplete="username"
              autoFocus
              onChange={(e) => setLogin({ ...loginId, user: e.target.value })}
            />
            <TextField
              variant="outlined"
              margin="normal"
              fullWidth
              name="password"
              label={<p>รหัสผ่าน</p>}
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={(e) =>
                setLogin({ ...loginId, password: e.target.value })
              }
            />

            <Link
              href={`${process.env.PUBLIC_URL}/admin/dash`}
              color="inherrit"
            >
              <Button
                type="submit"
                variant="contained"
                color="primary"
                className={classes.submit}
                onClick={onLogin()}
              ></Button>
            </Link>
          </form>
        </div>
      </Grid>
    </Grid>
  );
}
