import React from 'react'
import Navbar from '../../component/user/Navbar'
import Grid from '@material-ui/core/Grid'
import { red } from '@material-ui/core/colors'
import { green } from '@material-ui/core/colors'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import { createMuiTheme } from '@material-ui/core/styles'
import Button from '@material-ui/core/Button'
import { Alert, AlertTitle } from '@material-ui/lab'
import '../../App.css'
import Link from '@material-ui/core/Link'
import PlayArrowRoundedIcon from '@material-ui/icons/PlayArrowRounded'
import logo from '../../pic/transfer-to-ocsc.png'
import Checkbox from '@material-ui/core/Checkbox'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormGroup from '@material-ui/core/FormGroup';

const useStyles = makeStyles((theme) => ({}))

export default function Home() {
  const classes = useStyles()
  const theme = createMuiTheme({
    typography: {
      fontFamily: '"Prompt", serif',
    },
  })
  const GreenCheckbox = withStyles({
    root: {
      color: green[400],
      '&$checked': {
        color: green[600],
      },
    },
    checked: {},
  })((props) => <Checkbox color="default" {...props} />)
  const RedCheckbox = withStyles({
    root: {
      color: red[400],
      '&$checked': {
        color: red[600],
      },
    },
    checked: {},
  })((props) => <Checkbox color="default" {...props} />)
  const [state, setState] = React.useState({
    checkedG:"",
    checkedA:"",

  })

  const handleChange = (event) => {
    if(event.target.name===state.heckedG){
      setState({ ...state, [event.target.name]: event.target.checked })
      setState({ ...state, checkedA: "" })
    }
  else{

      setState({ ...state, [event.target.name]: event.target.checked })
      setState({ ...state, checkedG: "" })
    }


  }


  return (
    <div className="container" theme={theme}>
      <div className="row">
        <Navbar />

        <Container maxWidth="sm" alignItems="center">
          <div className="box">
            <Container fixed>
              <Box paddingTop={20}>
                <Grid
                  container
                  direction="row"
                  justify="center"
                  alignItems="center"
                  spacing={1}
                >
                  <Box
                    p={1}
                    bgcolor="background.paper"
                    boxShadow={3}
                    spacing={1}
                    style={{ borderRadius: '10px' }}
                  >
                    <div>
                      <h2>แบบประเมินความสนใจของบุคคลที่มีต่องานราชการไทย</h2>
                    </div>
                    <img src={logo} width={300} className="center" alt="logo" />
                    <div>
                      &nbsp;
                      &nbsp;&nbsp;&nbsp;สํานักงาน&nbsp;ก.พ.&nbsp;ร่วมกับ&nbsp;
                      กรมการจัดหางาน&nbsp;กระทรวงแรงงาน&nbsp;
